'use strict';function numberToString(n){var str=n.toString();return''+(str.length===1?'0':'')+str;}function getCLFOffset(date){var offset=date.getTimezoneOffset().toString();var op=offset[0]==='-'?'-':'+';var number=offset.replace(op,'');while(number.length<4){number='0'+number;}return''+op+number;}module.exports=function(){var now=arguments.length>0&&arguments[0]!==undefined?arguments[0]:new Date();if(!(now instanceof Date)){throw new Error('clf-date: invalid parameter');}var MONTHS=['Jan','Feb','Mar','Apr','May','Jun','Jul','Aug','Sep','Oct','Nov','Dec'];var day=numberToString(now.getUTCDate());var month=MONTHS[now.getUTCMonth()];var year=now.getUTCFullYear();var hours=numberToString(now.getUTCHours());var minutes=numberToString(now.getUTCMinutes());var seconds=numberToString(now.getUTCSeconds());var offset=getCLFOffset(now);return day+'/'+month+'/'+year+':'+hours+':'+minutes+':'+seconds+' '+offset;};